mapper.select:
/* OperationMapper.pcsOperationStatusChart */ SELECT time, deviceId, deviceNm,
${column}
FROM ( SELECT V_TIME AS time , V_DEVICE_ID AS deviceId , V_DEVICE_NM AS deviceNm , D_ACT_KW AS actKw , D_REACT_KW AS reactKw , D_BAT_DC_V AS batV , D_BAT_DC_A AS batA , D_BAT_DC_KW AS batKw , D_DC_LINK_A AS dcLinkA , D_DC_LINK_V AS dcLinkV , D_GRID_RS_V AS gridVRS , D_GRID_ST_V AS gridVST , D_GRID_TR_V AS gridVTR , D_GRID_R_A AS gridAR , D_GRID_S_A AS gridAS , D_GRID_T_A AS gridAT , D_IN_TEMP AS boxTemp , D_OUT_TEMP AS outTemp , D_PF AS gridPF , D_FREQ AS gridFreq , D_TOT_CHARGE_KWH AS totGridKwhOut , D_TOT_DISCHA_KWH AS totGridKwhIn FROM NRT_PCS_MINLY_STAT WHERE V_SITE_ID = ( SELECT V_SITE_ID FROM NST_PMS_SETTING WHERE V_CATEGORY = "sysOPMode" )
AND V_DEVICE_ID = #{deviceId}
ORDER BY V_TIME DESC LIMIT 90 ) AS DATA ORDER BY time ASC

/* OperationMapper.getBmsStatusChart */ SELECT time, deviceId, deviceNm,
${column}
FROM ( SELECT V_TIME AS time , V_DEVICE_ID AS deviceId , V_DEVICE_NM AS deviceNm , D_BAT_DC_V as batV , D_BAT_DC_A as batA , D_BAT_DC_KW as batKw , D_BAT_SOC as batSoc , D_BAT_SOH as batSoh , D_CELL_V_MAX as maxCellV -- 최대 셀 전압 , D_CELL_TEMP_MAX as maxCellT -- 최대 셀 온도 , D_CELL_V_MIN as minCellV -- 최소 셀 전압 , D_CELL_TEMP_MIN as minCellT -- 최소 셀 온도 , D_CELL_V_AVG as avgCellV -- 평균 셀 전압 , D_CELL_TEMP_AVG as avgCellT-- 평균 셀 온도 , D_CHARGE_LIMIT_KW as chaPowLimit , D_DISCHA_LIMIT_KW as disPowLimit FROM NRT_BMS_MINLY_STAT WHERE V_SITE_ID = ( SELECT V_SITE_ID FROM NST_PMS_SETTING WHERE V_CATEGORY = "sysOPMode" )
AND V_DEVICE_ID = #{deviceId}
ORDER BY V_TIME DESC LIMIT 90 ) AS DATA ORDER BY time ASC

/* OperationMapper.getPviStatusChart */ SELECT time, deviceId, deviceNm,
${column}
FROM ( SELECT V_TIME AS time , V_DEVICE_ID AS deviceId , V_DEVICE_NM AS deviceNm , D_ACT_KW AS actKw , D_REACT_KW AS reactKw , D_DC_V AS dcV , D_DC_A AS dcA , D_DC_KW AS dcKw , D_GRID_RS_V AS gridVRS , D_GRID_ST_V AS gridVST , D_GRID_TR_V AS gridVTR , D_GRID_R_A AS gridAR , D_GRID_S_A AS gridAS , D_GRID_T_A AS gridAT , D_IN_TEMP AS boxTemp , D_OUT_TEMP AS outTemp , D_PF AS gridPF , D_FREQ AS gridFreq FROM NRT_PVI_MINLY_STAT WHERE V_SITE_ID = ( SELECT V_SITE_ID FROM NST_PMS_SETTING WHERE V_CATEGORY = "sysOPMode" )
AND V_DEVICE_ID = #{deviceId}
ORDER BY V_TIME DESC LIMIT 90 ) AS DATA ORDER BY time ASC

/* OperationMapper.getThsStatusChart */ SELECT time, deviceId, deviceNm,
${column}
FROM ( SELECT V_TIME AS time , V_DEVICE_ID AS deviceId , V_DEVICE_NM AS deviceNm , D_SEN_01 as sen01 -- 온도 , D_SEN_02 as sen02 -- 습도 FROM NRT_THS_MINLY_STAT WHERE V_SITE_ID = ( SELECT V_SITE_ID FROM NST_PMS_SETTING WHERE V_CATEGORY = "sysOPMode" )
AND V_DEVICE_ID = #{deviceId}
ORDER BY V_TIME DESC LIMIT 90 ) AS DATA ORDER BY time ASC

/* OperationMapper.getPvsStatusChart */ SELECT time, deviceId, deviceNm,
${column}
FROM ( SELECT V_TIME AS time , V_DEVICE_ID AS deviceId , V_DEVICE_NM AS deviceNm , D_PERIOD_HORI_SOLAR as horiRadiation -- 수평 일사량 , D_PERIOD_SLOP_SOLAR as inclRadiation -- 경사 일사량 , D_TEMP AS temperature -- 온도 , D_HUMIDITY AS humidity -- 습도 FROM NRT_PVS_MINLY_STAT WHERE V_SITE_ID = ( SELECT V_SITE_ID FROM NST_PMS_SETTING WHERE V_CATEGORY = "sysOPMode" )
AND V_DEVICE_ID = #{deviceId}
ORDER BY V_TIME DESC LIMIT 90 ) AS DATA ORDER BY time ASC

/* OperationMapper.getEventStatusTable */
SELECT E.N_EVENT_SEQ AS eventSeq , E.V_START_TIME AS startTime , E.V_END_TIME AS endTime , E.V_DEVICE_ID AS deviceId , D.V_DEVICE_NM AS deviceNm , E.V_EVENT_LVL AS eventLvl , CD.V_CD_DESC AS eventLvlNm , E.V_EVENT_STATUS_CD AS eventStatusCd , CD2.V_CD_NM AS eventStatusNm , E.V_EVENT_NM AS eventNm , E.V_EVENT_DESC AS eventDesc FROM ( SELECT EVENT01.V_SITE_ID , EVENT01.N_EVENT_SEQ , EVENT01.V_START_TIME , IF(EVENT02.V_EVENT_STATUS_CD = 'SD005', EVENT02.V_END_TIME, EVENT01.V_END_TIME) AS V_END_TIME , EVENT01.V_DEVICE_ID , EVENT01.V_EVENT_LVL , IF(EVENT02.V_EVENT_STATUS_CD = 'SD005', EVENT02.V_EVENT_STATUS_CD, EVENT01.V_EVENT_STATUS_CD) AS V_EVENT_STATUS_CD , EVENT01.V_EVENT_NM , EVENT01.V_EVENT_DESC FROM NHT_EVENT AS EVENT01 LEFT JOIN ( SELECT V_DEVICE_ID, V_START_TIME, V_END_TIME, V_EVENT_STATUS_CD, V_EVENT_NM FROM NHT_EVENT WHERE V_EVENT_STATUS_CD = 'SD005' AND V_END_TIME != '' AND V_END_TIME IS NOT NULL ) AS EVENT02 ON EVENT01.V_DEVICE_ID = EVENT02.V_DEVICE_ID AND EVENT01.V_START_TIME = EVENT02.V_START_TIME AND EVENT01.V_EVENT_NM = EVENT02.V_EVENT_NM WHERE EVENT01.V_START_TIME LIKE CONCAT(DATE_FORMAT(NOW(), '%Y%m%d'),'%') AND EVENT01.V_EVENT_STATUS_CD != 'SD005'
AND EVENT01.V_GR_ID = #{grId}
AND EVENT01.V_EVENT_LVL = #{eventLvl}
) AS E LEFT OUTER JOIN NST_DEVICE_INFO D ON D.V_SITE_ID = E.V_SITE_ID AND D.V_DEVICE_ID = E.V_DEVICE_ID LEFT OUTER JOIN NST_CD_DETAIL CD ON CD.V_MASTER_CD = 'SM010' AND CD.V_DETAIL_CD = E.V_EVENT_LVL LEFT OUTER JOIN NST_CD_DETAIL CD2 ON CD2.V_MASTER_CD = 'SM049' AND CD2.V_DETAIL_CD = E.V_EVENT_STATUS_CD
AND E.V_EVENT_STATUS_CD = #{eventStatusCd}

/* OperationMapper.getEventStatusTableCnt */ SELECT COUNT(*) FROM ( SELECT EVENT01.V_SITE_ID , EVENT01.N_EVENT_SEQ , EVENT01.V_START_TIME , IF(EVENT02.V_EVENT_STATUS_CD = 'SD005', EVENT02.V_END_TIME, EVENT01.V_END_TIME) AS V_END_TIME , EVENT01.V_DEVICE_ID , EVENT01.V_EVENT_LVL , IF(EVENT02.V_EVENT_STATUS_CD = 'SD005', EVENT02.V_EVENT_STATUS_CD, EVENT01.V_EVENT_STATUS_CD) AS V_EVENT_STATUS_CD , EVENT01.V_EVENT_NM , EVENT01.V_EVENT_DESC FROM NHT_EVENT AS EVENT01 LEFT JOIN ( SELECT V_DEVICE_ID, V_START_TIME, V_END_TIME, V_EVENT_STATUS_CD, V_EVENT_NM FROM NHT_EVENT WHERE V_EVENT_STATUS_CD = 'SD005' AND V_END_TIME != '' AND V_END_TIME IS NOT NULL ) AS EVENT02 ON EVENT01.V_DEVICE_ID = EVENT02.V_DEVICE_ID AND EVENT01.V_START_TIME = EVENT02.V_START_TIME AND EVENT01.V_EVENT_NM = EVENT02.V_EVENT_NM WHERE EVENT01.V_START_TIME LIKE CONCAT(DATE_FORMAT(NOW(), '%Y%m%d'),'%')
AND EVENT01.V_GR_ID = #{grId}
AND EVENT01.V_EVENT_LVL = #{eventLvl}
) AS E
AND E.V_EVENT_STATUS_CD = #{eventStatusCd}
